settings.custom_css:
/* elementorfa CM Style */
/* 3 Coloumn Comment */
form#commentform {
    display: flex;
    flex-wrap: wrap;
}
p.comment-notes, p.comment-form-comment,p.comment-form-cookies-consent {
    flex-basis: 100%;
}
p.comment-form-author,p.comment-form-email,p.comment-form-url {
    flex-basis: 33.33%;
    padding: 0 5px 0 0;
}
p.form-submit {
    flex-basis: 100%;
}

/* Comment Meta */

.comment-meta {
    display: flex;
    flex-direction: row;
    flex-wrap: wrap;
}
#comments .comment-meta {
    flex-wrap: wrap;
    justify-content: right;
}
.comment-author.vcard {
    margin-left: 20px;
}
span.says {
    display: none;
}

/* Comment Style */

textarea#comment {
    display: block;
    width: 100%;
    padding: 6px 12px;
    font-size: 14px;
    line-height: 1.42857143;
    color: #555;
    background-color: #fff;
    background-image: none;
    border: 1px solid #ccc;
    border-radius: 4px;
    -webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,.075);
    box-shadow: inset 0 1px 1px rgba(0,0,0,.075);
    -webkit-transition: border-color ease-in-out .15s,-webkit-box-shadow ease-in-out .15s;
    -o-transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
    transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
}
textarea#comment:focus
{
    border-color: #66afe9;
    outline: 0;
    -webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,.075), 0 0 8px rgba(102,175,233,.6);
    box-shadow: inset 0 1px 1px rgba(0,0,0,.075), 0 0 8px rgba(102,175,233,.6);
}
input#author,input#email,input#url {
    display: block;
    width: 100%;
    padding: 6px 12px;
    font-size: 14px;
    line-height: 1.42857143;
    color: #555;
    background-color: #fff;
    background-image: none;
    border: 1px solid #ccc;
    border-radius: 4px;
    -webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,.075);
    box-shadow: inset 0 1px 1px rgba(0,0,0,.075);
    -webkit-transition: border-color ease-in-out .15s,-webkit-box-shadow ease-in-out .15s;
    -o-transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
    transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
}
input#author:focus,input#email:focus,input#url:focus {
        border-color: #66afe9;
    outline: 0;
    -webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,.075), 0 0 8px rgba(102,175,233,.6);
    box-shadow: inset 0 1px 1px rgba(0,0,0,.075), 0 0 8px rgba(102,175,233,.6);
}
input#submit {
    width: 100%;
    background: none;
    color: #333;
    border: 1px solid #ccc;
    box-shadow: none;
    text-shadow: none;
}
input#submit:hover {
background: #eee;
    box-shadow: none;
}
#comments .comment .comment-body, #comments .pingback .comment-body {
    border-bottom: 0px solid #ccc;
}
li.comment.even.thread-even.depth-1.parent {
    border-bottom: 1px solid #eee;
}
body.rtl #comments ol.comment-list .children:before,span.says {
    display: none
}
.comment-author.vcard a, .comment-author.vcard {
    color: #e51a2d;
}
.comment-content {
    color: #333;
    font-size: 14px;
}
.comment-metadata, .comment-metadata a {
    color: #999;
}
.comment-metadata:before {
    content: '\f017';
    margin-left: 5px;
    font-family: FontAwesome;
}
.reply, .reply a {
    color: #696969;
}
.reply:before {
    right: -4px;
    top: 1px;
    width: 40px;
    content: '\f086';
    height: 1px;
    font-family: FontAwesome;
    position: relative;
}
a#cancel-comment-reply-link {
    color: #e51a2d;
}

@media (max-width:768px) {
    body.rtl #comments .comment .comment-body, body.rtl #comments .pingback .comment-body {
    padding: 30px 70px 30px 10px;
}
}
@media (max-width:480px) {
    body.rtl #comments .comment .comment-body, body.rtl #comments .pingback .comment-body {
    padding: 30px 10px 30px 10px;
}
body.rtl #comments .comment .avatar, body.rtl #comments .pingback .avatar {
    position: relative;
    bottom: 10px;
}
}
