settings.custom_css:
/* elementorfa CM Style */
/* 3 Coloumn Comment Revers */
form#commentform {
    display: flex;
    flex-wrap: wrap;
    flex-direction: row
}
p.comment-notes, p.comment-form-comment,p.comment-form-cookies-consent {
    flex-basis: 100%;
}
p.comment-form-author {
    flex-basis: 33.33%;
    padding: 0 0 0 25px;
}
p.comment-form-email {
    flex-basis: 33.33%;
    padding: 0 12.5px 0 12.5px;
}
p.comment-form-url {
    flex-basis: 33.33%;
    padding: 0 25px 0 0;
}

/* Comment Meta */

.comment-meta {
    display: flex;
    flex-direction: row;
    flex-wrap: wrap;
}
#comments .comment-meta {
    flex-wrap: wrap;
    justify-content: right;
}
.comment-author.vcard {
    margin-left: 20px;
}
span.says {
    display: none;
}

/* Comment Style */

textarea#comment,input#email,input#author,input#url {
background-color: #fff;
    border: 2px solid #e2e2e2;
    border-radius: 5px;
    resize: none;
    margin-top: 4px;
    margin-bottom: 8px;
    outline: 0;
    max-width: 100%;
    padding: 10px 12px;
    font-size: 12px;
    color: #666;
    line-height: 16px;

}
input#submit {
height: 38px;
    padding: 0 22px;
    line-height: 38px;
    display: inline-block;
    border-radius: 30px;
    -webkit-border-radius: 30px;
    -moz-border-radius: 30px;
    border: 1px solid #ddd;
    background-color: #fff;
    color: inherit;
    -o-transition: all 0.15s ease-in;
    -webkit-transition: all 0.15s ease-in;
    -moz-transition: all 0.15s ease-in;
    transition: all 0.15s ease-in;
}
input#submit:hover {
background-color: #F2F2F2;
    border-color: #f2f2f2;
}
body.rtl #comments .comment .comment-body, body.rtl #comments .pingback .comment-body {
    padding: 30px 60px 30px 30px;
    background-color: #FFF
}

ol.children {
    border-bottom: 1px dotted #ddd;
}
#comments .comment .comment-body, #comments .pingback .comment-body {

    border-bottom: 0px solid #ccc;
}
footer.comment-meta {
    border-right: 2px solid #ececec;
    padding-right: 10px;
}
body.rtl #comments ol.comment-list .children:before {
display: none
}
.comment-author.vcard {
    flex-basis: 100%;
}
.comment-author.vcard {
    flex-basis: 100%;
}
.comment-content {
    margin-top: 10px;
}
.comment-metadata, .comment-metadata a {
    color: #acabab;
    font-size: 12px;
        margin-top: 10px;
}
.reply, .reply a {
    color: #f7cb3d;
    font-size: 12px
}
.comment-author.vcard, .comment-author.vcard a {
    color: #000;
    font-size: 15px;
}
@media (max-width:479px){
    body.rtl #comments .comment .comment-body, body.rtl #comments .pingback .comment-body {
    padding: 30px 10px 30px 30px;
}
p.comment-form-author,p.comment-form-email,p.comment-form-url {
    flex-basis: 100%;
    padding: 0;
}
body.rtl #comments .comment .avatar, body.rtl #comments .pingback .avatar {
    position: relative;
    bottom: 10px;
}
footer.comment-meta {
    border-right: 0px solid #ececec;
    padding-right: 00px;
}
}
